import React, { useEffect, useState } from 'react'
import styled from 'styled-components'

type ContainerProps = {}
type ComponentProps = {
  className: string
  title: any
} & ContainerProps

const Component: React.FC<ComponentProps> = props => (
  <div className={props.className}>{props.title}</div>
)

const StyledComponent = styled(Component)``

const Container: React.FC<ContainerProps> = props => {
  const [title, setTitle] = useState<any>('')

  const getTitle = async (): Promise<string> => {
    const key = {
      headers: { 'X-API-KEY': '424e3496-6cb7-4ebd-8572-49b5a148e90f' }
    }
    const res = await fetch('https://nepota.microcms.io/api/v1/blog', key)
    const repos = await res.json()
    return repos.contents[1].titles
  }

  useEffect(() => {
    const func = async () => {
      const str = await getTitle()
      setState(str)
    }
    func()
  }, [])

  return <StyledComponent className="index" title={title} {...props} />
}

export default Container
